import React, {Component} from 'react';
// import mp3No from "../../../public/music/no.mp3"
var no = new Audio("");

class ImageList extends Component {
    constructor(props) {
        super(props);
        this.state = {
            no: new Audio(no)
        }
    }

    playAudio = (color) => {
        var no = new Audio("/music/no.mp3");
        var yes = new Audio('/music/yes.mp3');
        if (this.props.code === color) {
            yes.play();
        } else {
            no.play();
        }
    }

    render() {

        return (
            <>
                <div id="img1" style={{textAlign: 'center', marginTop: "100px"}}>
                    <a onClick={() => this.playAudio("cam")}>
                        <img src="/image/cam.jpg" id="cam" width="100px" height="100px "
                             style={{borderRadius: '50%', marginRight: 50, marginBottom: 50}}/>
                    </a>
                    <a onClick={() => this.playAudio("den")}>
                        <img src="/image/den.jpg" id="den" width="100px" height="100px "
                             style={{borderRadius: '50%', marginRight: 50, marginBottom: 50}}/>
                    </a>
                    <a onClick={() => this.playAudio("do")}>
                        <img src="/image/do.jpg" id="do" width="100px" height="100px "
                             style={{borderRadius: '50%', marginRight: 50, marginBottom: 50}}/>
                    </a>
                    <a onClick={() => this.playAudio}>
                        <img src="/image/hong.jpg" id="hong" width="100px" height="100px "
                             style={{borderRadius: '50%', marginRight: 50, marginBottom: 50}}/>
                    </a>
                    <a onClick={() => this.playAudio}>
                        <img src="/image/nau.png" id="nau" width="100px" height="100px "
                             style={{borderRadius: '50%', marginRight: 50, marginBottom: 50}}/><br/>
                    </a>
                    <a onClick={() => this.playAudio}>
                        <img src="/image/tim.webp" id="tim" width="100px" height="100px "
                             style={{borderRadius: '50%', marginRight: 50, marginBottom: 50}}/>
                    </a>
                    <a onClick={() => this.playAudio}>
                        <img src="/image/vang.jpg" id="vang" width="100px" height="100px "
                             style={{borderRadius: '50%', marginRight: 50, marginBottom: 50}}/>
                    </a>
                    <a onClick={() => this.playAudio}>
                        <img src="/image/xanhd.jpg" id="xanhd" width="100px" height="100px "
                             style={{borderRadius: '50%', marginRight: 50, marginBottom: 50}}/>
                    </a>
                    <a onClick={() => this.playAudio}>
                        <img src="/image/xanhl.png" id="xanhl" width="100px" height="100px "
                             style={{borderRadius: '50%', marginRight: 50, marginBottom: 50}}/>
                    </a>
                    <a onClick={() => this.playAudio}>
                        <img src="/image/xám.jpg" id="xam" width="100px" height="100px "
                             style={{borderRadius: '50%', marginRight: 50, marginBottom: 50}}/>
                    </a>
                </div>
                <button id="next1" style={{
                    backgroundColor: 'greenyellow',
                    display: 'none',
                    borderRadius: 10,
                    width: 100,
                    height: 50,
                    marginLeft: 1300
                }}>NEXT
                </button>
            </>
        );
    }
}

export default ImageList;
