import React, {Component} from 'react';
import ImageList from "./ImageList";
import Alphabet from "./Alphabet";

class HomeEnglish extends Component {
    constructor(props) {
        super(props);
        this.state = {
            colors: [{
                imgUrl: "/image/cam.jpg",
                keyword: "orange"
            }],
            color: {
                url: "",
                code: ""
            }

        }
    }

    componentDidMount() {
        const chu = this.state.colors.find(color => color.keyword === this.props.match.params.color);
        console.log(chu)
        if (chu) {
            this.setState({
                color: {
                    url: chu.imgUrl,
                    code: chu.keyword
                }
            })
        }
    }

    render() {
        return (
            <div>
                <div>
                    <h1 className="game-title">Play-Game</h1>
                </div>
                <div>
                    <h1 className="win" style={{display: 'none', marginTop: 350}}>WIN</h1>
                </div>
                <ImageList
                    url={this.state.color.url}
                    code={this.state.color.code}
                />
                <Alphabet/>
            </div>
        );
    }
}

export default HomeEnglish;
